function convertLeadToSlackBlocks(leadData) {
  const blocks = [];

  // Header section
  blocks.push({
    type: 'header',
    text: {
      type: 'plain_text',
      text: `üë§ Lead Information: ${leadData.full_name || leadData.email}`,
      emoji: true
    }
  });

  // Basic Information
  blocks.push({
    type: 'section',
    fields: [
      {
        type: 'mrkdwn',
        text: `*Email:* ${leadData.email}`
      },
      {
        type: 'mrkdwn',
        text: `*Name:* ${leadData.full_name || 'N/A'}`
      }
    ]
  });

  // HubSpot Data
  if (leadData.hubspot_data) {
    blocks.push({ 'type': 'divider' });
    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üìä HubSpot Data*'
      }
    });

    const hubspotFields = [
      { type: 'mrkdwn', text: `*Lifecycle Stage:* ${leadData.hubspot_data.LifecycleStage || 'N/A'}` },
      { type: 'mrkdwn', text: `*Created:* ${leadData.hubspot_data.CreateDate || 'N/A'}` },
      { type: 'mrkdwn', text: `*Last Modified:* ${leadData.hubspot_data.LastModifiedDate || 'N/A'}` },
      { type: 'mrkdwn', text: `*Last Sales Activity:* ${leadData.hubspot_data.LastSalesActivityDate || 'N/A'} (${leadData.hubspot_data.LastSalesActivityType || 'N/A'})` },
      { type: 'mrkdwn', text: `*Is Unworked:* ${leadData.hubspot_data.IsUnworked || 'N/A'}` },
      { type: 'mrkdwn', text: `*First Engagement:* ${leadData.hubspot_data.FirstEngagementDate || 'N/A'} (${leadData.hubspot_data.FirstEngagementObjectType || 'N/A'})` },
      { type: 'mrkdwn', text: `*Last Contacted:* ${leadData.hubspot_data.LastContactedDate || 'N/A'}` },
      { type: 'mrkdwn', text: `*Latest Meeting:* ${leadData.hubspot_data.LatestMeetingActivity || 'N/A'}` }
    ];

    // Split HubSpot fields into two columns
    const midpoint = Math.ceil(hubspotFields.length / 2);
    blocks.push({
      type: 'section',
      fields: hubspotFields.slice(0, midpoint)
    });
    blocks.push({
      type: 'section',
      fields: hubspotFields.slice(midpoint)
    });

    // Associated Company
    if (leadData.hubspot_data.AssociatedCompany) {
      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: '*üè¢ Associated Company*'
        }
      });

      const companyFields = [
        { type: 'mrkdwn', text: `*Name:* ${leadData.hubspot_data.AssociatedCompany.Name || 'N/A'}` },
        { type: 'mrkdwn', text: `*Domain:* ${leadData.hubspot_data.AssociatedCompany.Domain || 'N/A'}` },
        { type: 'mrkdwn', text: `*Revenue:* $${(leadData.hubspot_data.AssociatedCompany.AnnualRevenue / 1000000).toFixed(1)}M` },
        { type: 'mrkdwn', text: `*Employees:* ${leadData.hubspot_data.AssociatedCompany.NumberOfEmployees || 'N/A'}` },
        { type: 'mrkdwn', text: `*Industry:* ${leadData.hubspot_data.AssociatedCompany.Industry || 'N/A'}` },
        { type: 'mrkdwn', text: `*Founded:* ${leadData.hubspot_data.AssociatedCompany.Founded || 'N/A'}` },
        { type: 'mrkdwn', text: `*Location:* ${[leadData.hubspot_data.AssociatedCompany.City, leadData.hubspot_data.AssociatedCompany.State, leadData.hubspot_data.AssociatedCompany.Country].filter(Boolean).join(', ')}` },
        { type: 'mrkdwn', text: `*Phone:* ${leadData.hubspot_data.AssociatedCompany.Phone || 'N/A'}` }
      ];

      // Split company fields into two columns
      const companyMidpoint = Math.ceil(companyFields.length / 2);
      blocks.push({
        type: 'section',
        fields: companyFields.slice(0, companyMidpoint)
      });
      blocks.push({
        type: 'section',
        fields: companyFields.slice(companyMidpoint)
      });

      // Company description
      if (leadData.hubspot_data.AssociatedCompany.Description) {
        blocks.push({
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: `*Description:* ${leadData.hubspot_data.AssociatedCompany.Description}`
          }
        });
      }

      // Company social links
      const socialLinks = [];
      if (leadData.hubspot_data.AssociatedCompany.LinkedInUrl) socialLinks.push(`<${leadData.hubspot_data.AssociatedCompany.LinkedInUrl}|LinkedIn>`);
      if (leadData.hubspot_data.AssociatedCompany.FacebookUrl) socialLinks.push(`<${leadData.hubspot_data.AssociatedCompany.FacebookUrl}|Facebook>`);
      if (leadData.hubspot_data.AssociatedCompany.TwitterHandle) socialLinks.push(`<https://twitter.com/${leadData.hubspot_data.AssociatedCompany.TwitterHandle}|Twitter>`);

      if (socialLinks.length > 0) {
        blocks.push({
          type: 'context',
          elements: [
            {
              type: 'mrkdwn',
              text: `*Social:* ${socialLinks.join(' | ')}`
            }
          ]
        });
      }
    }

    // List Memberships
    if (leadData.hubspot_data.ListMemberships && leadData.hubspot_data.ListMemberships.length > 0) {
      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: `*üìã List Memberships:* ${leadData.hubspot_data.ListMemberships.map(list => list.ListId).join(', ')}`
        }
      });
    }
  }

  // LinkedIn Information
  if (leadData.linkedin_info) {
    blocks.push({ 'type': 'divider' });

    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üîó LinkedIn Information*'
      }
    });

    const linkedinFields = [
      {
        type: 'mrkdwn',
        text: `*Profile:* ${leadData.linkedin_info.profile_url ? `<${leadData.linkedin_info.profile_url}|View Profile>` : 'N/A'}`
      },
      {
        type: 'mrkdwn',
        text: `*Current Position:* ${leadData.linkedin_info.current_position || 'N/A'}`
      }
    ];

    if (leadData.linkedin_info.groups && leadData.linkedin_info.groups.length > 0) {
      linkedinFields.push({
        type: 'mrkdwn',
        text: `*Groups:* ${leadData.linkedin_info.groups.join(', ')}`
      });
    }

    blocks.push({
      type: 'section',
      fields: linkedinFields
    });

    // Recent LinkedIn Posts
    if (leadData.linkedin_info.recent_posts && leadData.linkedin_info.recent_posts.length > 0) {
      blocks.push({ 'type': 'divider' });

      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: '*üìù Recent LinkedIn Posts*'
        }
      });

      leadData.linkedin_info.recent_posts.forEach(post => {
        blocks.push({
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: `‚Ä¢ _${post.date}:_ ${post.content.substring(0, 100)}${post.content.length > 100 ? '...' : ''} <${post.url}|(link)>`
          }
        });
      });
    }
  }

  // Blog Articles
  if (leadData.blog_articles && leadData.blog_articles.length > 0) {
    blocks.push({ 'type': 'divider' });
    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üìö Blog Articles*'
      }
    });

    leadData.blog_articles.forEach(article => {
      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: `‚Ä¢ _${article.date}_: ${article.title} <${article.url}|(link)>`
        }
      });
    });
  }

  // Company Information
  if (leadData.company) {
    blocks.push({ 'type': 'divider' });
    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üè¢ Company Information*'
      }
    });

    const companyFields = [
      {
        type: 'mrkdwn',
        text: `*Name:* ${leadData.company.name || 'N/A'}`
      },
      {
        type: 'mrkdwn',
        text: `*Industry:* ${leadData.company.industry || 'N/A'}`
      },
      {
        type: 'mrkdwn',
        text: `*Size:* ${leadData.company.size || 'N/A'}`
      },
      {
        type: 'mrkdwn',
        text: `*Website:* ${leadData.company.website ? `<${leadData.company.website}|Visit Site>` : 'N/A'}`
      },
      {
        type: 'mrkdwn',
        text: `*LinkedIn:* ${leadData.company.linkedin_url ? `<${leadData.company.linkedin_url}|View Company>` : 'N/A'}`
      }
    ];

    blocks.push({
      type: 'section',
      fields: companyFields
    });
  }

  // Company Leaders
  if (leadData.company_leaders && leadData.company_leaders.length > 0) {
    blocks.push({ 'type': 'divider' });
    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üë• Company Leaders*'
      }
    });

    leadData.company_leaders.forEach(leader => {
      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: `‚Ä¢ *${leader.name}* - ${leader.position}${leader.linkedin_url ? ` (<${leader.linkedin_url}|LinkedIn>)` : ''}`
        }
      });
    });
  }

  // Final Summary
  if (leadData.final_summary) {
    blocks.push({ type: 'divider' });

    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: '*üéØ Final Summary*'
      }
    });

    const summaryEmoji = {
      high: 'üü¢',
      medium: 'üü°',
      low: 'üî¥'
    };

    blocks.push({
      type: 'context',
      elements: [
        {
          type: 'mrkdwn',
          text: `*Lead Potential:* ${summaryEmoji[leadData.final_summary.lead_potential.toLowerCase()]} ${leadData.final_summary.lead_potential.toUpperCase()}`
        }
      ]
    });

    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `*Assessment:* ${leadData.final_summary.assessment}`
      }
    });

    if (leadData.final_summary.recommended_action) {
      blocks.push({ type: 'divider' });
      blocks.push({
        type: 'section',
        text: {
          type: 'mrkdwn',
          text: `*Recommended Action:*\n> ${leadData.final_summary.recommended_action}`
        }
      });
    }
  }

  return blocks;
}

