// Function to convert wins to Slack blocks
function convertWinsToSlackBlocks(data) {
  const blocks = [];

  // Header section
  blocks.push({
    type: 'header',
    text: {
      type: 'plain_text',
      text: 'ðŸŽ‰ Team Wins',
      emoji: true
    }
  });

  // Iterate over each win and construct blocks
  data.forEach(win => {
    blocks.push({ type: 'divider' });

    // Fun message section
    blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `*${win.generated_fun_message}*`
      }
    });

    // Details fields
    const fields = [];

    if (win.when) {
      fields.push({
        type: 'mrkdwn',
        text: `*Date:*\n${win.when}`
      });
    }

    if (win.who) {
      fields.push({
        type: 'mrkdwn',
        text: `*Who:*\n${win.who}`
      });
    }

    if (win.original_message) {
      fields.push({
        type: 'mrkdwn',
        text: `*Original Message:*\n${win.original_message}`
      });
    }

    if (win.links && win.links.length > 0) {
      const linksText = win.links.map(link => `<${link}|Link>`).join('\n');
      fields.push({
        type: 'mrkdwn',
        text: `*Links:*\n${linksText}`
      });
    }

    if (fields.length > 0) {
      blocks.push({
        type: 'section',
        fields: fields
      });
    }
  });

  return blocks;
}

