// Initialize an array to collect output items
const output = [];

// Loop over input items
for (const item of $input.all()) {
  // Access the data from each input item
  const data = item.json;

  // Extract the wins array from the data
  const wins = data.output?.wins || [];

  // Generate Slack blocks using the function
  const blocks = convertWinsToSlackBlocks(wins);

  // Add the blocks to the output item
  output.push({
    json: {
      blocks: blocks
    }
  });
}

// Return the output items
return output;

