function extractKeyContactData(contactData) {
    // Helper function to safely get nested values
    const getNested = (obj, ...args) => {
        return args.reduce((obj, level) => obj && obj[level], obj);
    };

    // Helper function to convert timestamp to date string
    const timestampToDate = (ts) => {
        return ts ? new Date(parseInt(ts)).toISOString().split('T')[0] : null;
    };

    const properties = contactData.properties || {};

    const extractedData = {
        Name: `${getNested(properties, 'firstname', 'value') || ''} ${getNested(properties, 'lastname', 'value') || ''}`.trim(),
        Email: getNested(properties, 'email', 'value'),
        JobTitle: getNested(properties, 'jobtitle', 'value'),
        Company: getNested(properties, 'company', 'value'),
        LifecycleStage: getNested(properties, 'lifecyclestage', 'value'),
        CreateDate: timestampToDate(getNested(properties, 'createdate', 'value')),
        LastModifiedDate: timestampToDate(getNested(properties, 'lastmodifieddate', 'value')),
        HubSpotOwnerId: getNested(properties, 'hubspot_owner_id', 'value'),
        LastSalesActivityDate: timestampToDate(getNested(properties, 'hs_last_sales_activity_timestamp', 'value')),
        LastSalesActivityType: getNested(properties, 'hs_last_sales_activity_type', 'value'),
        NumberOfPageViews: getNested(properties, 'hs_analytics_num_page_views', 'value'),
        NumberOfWebsiteVisits: getNested(properties, 'hs_analytics_num_visits', 'value'),
        IsContact: getNested(properties, 'hs_is_contact', 'value'),
        IsUnworked: getNested(properties, 'hs_is_unworked', 'value'),
        TimeToFirstEngagement: getNested(properties, 'hs_time_to_first_engagement', 'value'),
        FirstOutreachDate: timestampToDate(getNested(properties, 'hs_first_outreach_date', 'value')),
        FirstEngagementDate: timestampToDate(getNested(properties, 'hs_sa_first_engagement_date', 'value')),
        FirstEngagementObjectType: getNested(properties, 'hs_sa_first_engagement_object_type', 'value'),
        FirstEngagementObjectId: getNested(properties, 'hs_first_engagement_object_id', 'value'),
        NumberOfSalesEmailOpens: getNested(properties, 'hs_email_open', 'value'),
        LastSalesEmailOpenDate: timestampToDate(getNested(properties, 'hs_sales_email_last_opened', 'value')),
        LastSalesEmailReplyDate: timestampToDate(getNested(properties, 'hs_sales_email_last_replied', 'value')),
        NumberOfNotes: getNested(properties, 'num_notes', 'value'),
        NumberOfContactedNotes: getNested(properties, 'num_contacted_notes', 'value'),
        LastContactedDate: timestampToDate(getNested(properties, 'notes_last_contacted', 'value')),
        LastNotesUpdateDate: timestampToDate(getNested(properties, 'notes_last_updated', 'value')),
        LatestMeetingActivity: timestampToDate(getNested(properties, 'hs_latest_meeting_activity', 'value')),
    };

    // Extract associated company data
    const company = contactData['associated-company'] || {};
    extractedData.AssociatedCompany = {
        Name: getNested(company, 'properties', 'name', 'value'),
        Domain: getNested(company, 'properties', 'domain', 'value'),
        AnnualRevenue: getNested(company, 'properties', 'annualrevenue', 'value'),
        NumberOfEmployees: getNested(company, 'properties', 'numberofemployees', 'value'),
        Industry: getNested(company, 'properties', 'industry', 'value'),
        Founded: getNested(company, 'properties', 'founded_year', 'value'),
        City: getNested(company, 'properties', 'city', 'value'),
        State: getNested(company, 'properties', 'state', 'value'),
        Country: getNested(company, 'properties', 'country', 'value'),
        Phone: getNested(company, 'properties', 'phone', 'value'),
        Description: getNested(company, 'properties', 'description', 'value'),
        LinkedInUrl: getNested(company, 'properties', 'linkedin_company_page', 'value'),
        FacebookUrl: getNested(company, 'properties', 'facebook_company_page', 'value'),
        TwitterHandle: getNested(company, 'properties', 'twitterhandle', 'value'),
        TotalMoneyRaised: getNested(company, 'properties', 'total_money_raised', 'value'),
        IsPublic: getNested(company, 'properties', 'is_public', 'value'),
    };

    // Extract list memberships
    extractedData.ListMemberships = Array.isArray(contactData['list-memberships']) 
        ? contactData['list-memberships'].map(list => ({
            ListId: list['static-list-id'],
            JoinDate: timestampToDate(list.timestamp),
        }))
        : [];

    return extractedData;
}


return { "response": extractKeyContactData($('HubSpot1').item.json) }
