function parseSlackMessageUrl(url) {
    // Use a regular expression to extract the channel ID and timestamp
    const regex = /\/archives\/([A-Z0-9]+)\/p(\d{16})/;
    const match = url.match(regex);

    if (!match) {
        throw new Error('Invalid Slack message URL');
    }

    const channelId = match[1];
    let tsString = match[2]; // e.g., '1234567898000159'

    // Insert a '.' after the 10th digit to format the timestamp correctly
    tsString = tsString.slice(0, 10) + '.' + tsString.slice(10);

    return {
        channel: channelId,
        ts: tsString
    };
}


