// Function to clean up the text (handles user mentions and links)
function cleanText(text) {
    // Replace user mentions from <@USERID> to @USERID
    text = text.replace(/<@([A-Z0-9]+)>/g, '@$1');

    // Replace links from <http://example.com|example> to example (http://example.com)
    text = text.replace(/<([^|>]+)\|([^>]+)>/g, '$2 ($1)');

    // Replace links from <http://example.com> to http://example.com
    text = text.replace(/<([^|>]+)>/g, '$1');

    return text;
  }

  // Function to process each message item
  function processMessage(item) {
    if (!item) {
      return '';
    }

    // Determine the sender
    let sender = '';
    if (item.user) {
      sender = item.user;
    } else if (item.bot_profile && item.bot_profile.name) {
      sender = item.bot_profile.name;
    } else {
      sender = 'Unknown Sender';
    }

    // Use the text property
    let text = item.text || '';

    // Clean up the text for readability
    text = cleanText(text);

    // Append reactions if they exist
    if (item.reactions && item.reactions.length > 0) {
      text = `${text}
  --- reactions
  ${item.reactions.map(r => `${r.name} (${r.users.join(',')})`).join(', ')}
  --- end reactions
      `;
    }
    let ret = `${sender}: ${text.trim()}`

    // ret = `${ret}\n<|-(${$('Code').first().json.channel},${$('Code').first().json.ts})-|>`

    return ret;
  }

  // Initialize an empty array to collect the output strings
  let output = [];

  // Loop through all input items
  for (const item of $input.all()) {
    // Process each message and get the text
    let itemJSON = item.json;
    console.log("itemJSON", itemJSON);
    let messageText = processMessage(itemJSON);

    // If text is not empty, add it to the output array
    if (messageText) {
      output.push({ "text": messageText });
    }
  }

  // Return the array of processed message texts
  console.log("input.all", $input.all());
  console.log("output", output);
  return output;

function combineMessages(messages) {
  if (!Array.isArray(messages)) {
    throw new Error('Input must be an array');
  }

  return messages
    .map(message => message.json.text)
    .filter(text => text) // Remove undefined or null texts
    .join('\n');
}

const allInputs = $input.all();
const combinedMessages = combineMessages($input.all());
console.log("allInputs",allInputs);
console.log("combinedMessages", combinedMessages);
return [{ "result":combineMessages($input.all())}]

let messages = [];
let previousTexts = new Set();

for (const itemWrapper of $input.all()) {
  let item = itemWrapper.json; // item is one message in the thread
  let text = item.text || '';
  text = text.trim();

  if (!text) continue; // Skip empty messages
  if (previousTexts.has(text)) continue; // Skip duplicate messages
  previousTexts.add(text);


  // Clean up text
  // Remove mentions like <@U123ABC>
  text = text.replace(/<@U[A-Z0-9]+>/g, '').trim();
  // Remove lines starting with "Automated with this"
  text = text.replace(/_Automated with this .*/g, '').trim();
  // Remove any remaining underscores or tildes used for formatting
  text = text.replace(/[_~]/g, '');

  if (!text) continue; // Skip if text is empty after cleaning

  messages.push(text);
}

return { "data": messages };

