let hasItems = false;
all_items.forEach(item => {
  if(item.json.document.metadata.url == loop_item.json.loc){
    hasItems = true;
  }
});

return { 
  "url_exists_in_store": hasItems,
  "url_to_check": loop_item.json.loc
};

const url = $input.first().json.loc;
const lastmod = $input.first().json.lastmod;
const md = $input.last().json.md;

let newContent = `
--- Chunk Metadata
location(aka: url): ${url}
lastmod (aka: last_modified): ${lastmod}
--- End Chunk Metadata
${md}
`

return [{ "json": { 
  "content": newContent,
  "metadata": {
    "url": url,
    "last_modified": lastmod
  }
} }];
