function _log() {
    console.log("");
    console.log("---");
    console.log(...arguments);
    console.log("---");
    console.log("");
  }

  _log("this",this)

  // 1. Get Inputs
  const chunks = this.getInputData();
  _log("chunks", chunks);

  //assume the user query on the first result is the same for all (for now)
  const userQuery = chunks[0].json.query;

  const embedding = await this.getInputConnectionData('ai_embedding', 0);
  _log("embeddings",embedding)  


  const llm = await this.getInputConnectionData('ai_languageModel', 0);
  _log("llm",llm);

  const systemPrompt = `
You are an advanced language model designed to provide precise and accurate answers based on the provided context. Your goal is to synthesize information from the given documents to answer the user's query comprehensively. 

**Instructions:**
1. **Understand the Context:** Carefully read and comprehend the provided context.
2. **Answer Accurately:** Generate a clear and concise response that directly addresses the user's question using information from the context.
3. **Cite Sources:** Always, reference the sources of your information by including their URLs in markdown format, e.g., [Source Title](URL).
4. **Maintain Objectivity:** Ensure that your response is unbiased and based solely on the provided context.
5. **Avoid Hallucinations:** Do not include information that is not present in the context.

**Context:**
{context}

**User Question:**
{question}
  `;

  // 3. Implement OpenAI Rerank Logic
  async function rerankDocumentsOpenAI(chunks) {
      const documents = chunks.map((chunk) => chunk.json.document);
      const documentTexts = documents.map((doc) => doc.pageContent);

      _log("rerankDocumentsOpenAI", chunks, "documents", documents, "documentTexts", documentTexts);
    // Get the embedding of the query
    const queryEmbedding = await embedding.embedQuery(userQuery);


    // Get the embeddings of all documents
    const documentEmbeddings = await embedding.embedDocuments(documentTexts);

    // Compute cosine similarities and assign scores
    for (let i = 0; i < documents.length; i++) {
      const docEmbedding = documentEmbeddings[i];
      const score = cosineSimilarity(queryEmbedding, docEmbedding);

      // Update the chunk's score
      chunks[i].json.score = score;
      // Optionally, include the score in the document's metadata
      documents[i].metadata.score = score;
    }

    // Sort chunks by score descending
    chunks.sort((a, b) => b.json.score - a.json.score);

    // Extract the documents from the sorted chunks
    const rerankedDocuments = chunks.map((chunk) => chunk.json.document);

    return rerankedDocuments;
  }

  // Helper function to compute cosine similarity
  function cosineSimilarity(vecA, vecB) {
    let dotProduct = 0;
    let normA = 0;
    let normB = 0;
    for (let i = 0; i < vecA.length; i++) {
      dotProduct += vecA[i] * vecB[i];
      normA += vecA[i] ** 2;
      normB += vecB[i] ** 2;
    }
    return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
  }

  // 4. Rerank the documents
  const rerankedDocuments = await rerankDocumentsOpenAI(chunks);
  _log("rerankedDocuments", rerankedDocuments);

  const context = rerankedDocuments.map(doc => doc.pageContent).join("\n\n");

  // 5. Set up the prompt and chain
  const { ChatPromptTemplate } = require("@langchain/core/prompts");

  const prompt = ChatPromptTemplate.fromMessages([
    ["system", `${systemPrompt}\nYou have the following context:\n{context}\n\n`],
    ["human", "{question}"],
  ]);

  const chain = prompt.pipe(llm)

  // 6. Use the chain with the reranked documents
  const output = await chain.invoke({
    context: context,
    question: userQuery
  });

  return [{ json: { output } }];

