function parseSlackMessageUrl(url) {
    // Use a regular expression to extract the channel ID and timestamp
    const regex = /\/archives\/([A-Z0-9]+)\/p(\d{16})/;
    const match = url.match(regex);

    if (!match) {
        throw new Error('Invalid Slack message URL');
    }

    const channelId = match[1];
    let tsString = match[2]; // e.g., '1234567898000159'

    // Insert a '.' after the 10th digit to format the timestamp correctly
    tsString = tsString.slice(0, 10) + '.' + tsString.slice(10);

    return {
        channel: channelId,
        ts: tsString
    };
}


let url = $input.first().json.url;

console.log("url",url);

const parseResult = parseSlackMessageUrl(url);

console.log("parseResult", parseResult);
return parseResult;

// Function to extract text and sender from blocks
function extractTextAndSenderFromBlocks(blocks) {
  let text = '';
  let sender = '';

  if (!blocks || !Array.isArray(blocks)) {
    return { sender, text };
  }

  for (const block of blocks) {
    if (block.type === 'rich_text' && block.elements) {
      for (const element of block.elements) {
        if (element.type === 'rich_text_section' && element.elements) {
          for (const subElement of element.elements) {
            if (subElement.type === 'text') {
              let styledText = subElement.text;

              if (subElement.style) {
                if (subElement.style.bold) {
                  // If sender is not set, use the first bold text as sender
                  if (!sender) {
                    sender = styledText.trim();
                  } else {
                    // Apply bold styling
                    styledText = `**${styledText}**`;
                  }
                }
                if (subElement.style.italic) {
                  styledText = `*${styledText}*`;
                }
                if (subElement.style.code) {
                  styledText = `\`${styledText}\``;
                }
              }

              text += styledText;
            } else if (subElement.type === 'user') {
              text += `@${subElement.user_id}`;
            } else if (subElement.type === 'emoji') {
              text += `:${subElement.name}:`;
            }
            // Add more types if necessary
          }
        }
      }
    }
  }

  return { sender, text };
}

// Function to process each message item
function processMessage(item) {
  if (!item) {
    return '';
  }

  let sender = '';
  let text = '';

  if (item.blocks && item.blocks.length > 0) {
    const result = extractTextAndSenderFromBlocks(item.blocks);
    sender = result.sender;
    text = result.text;
  } else if (item.text) {
    text = item.text;
  } else {
    // No text found
    return '';
  }

  // Use user ID if sender's name is not available
  if (!sender) {
    sender = item.user || 'Unknown Sender';
  }

  return `${sender}: ${text.trim()}`;
}

// Initialize an empty array to collect the output strings
let output = [];

// Loop through all input items
for (const item of $input.all()) {
  // Process each message and get the text
  let itemJSON = item.json;
  console.log("itemJSON", itemJSON)
  let messageText = processMessage(itemJSON);

  // If text is not empty, add it to the output array
  if (messageText) {
    output.push({ "text" : messageText });
  }
}

// Return the array of processed message texts
console.log("input.all", $input.all())
console.log("output", output)
return output;

function combineMessages(messages) {
  if (!Array.isArray(messages)) {
    throw new Error('Input must be an array');
  }

  return messages
    .map(message => message.json.text)
    .filter(text => text) // Remove undefined or null texts
    .join('\n');
}

const allInputs = $input.all();
const combinedMessages = combineMessages($input.all());
console.log("allInputs",allInputs);
console.log("combinedMessages", combinedMessages);
return [{ "result":combineMessages($input.all())}]

